INSERT INTO `v9_python_test` VALUES ('363', '12', '0', '微软新大招？高通CPU能运行Win 10还能玩x86应用', '', '', '微软', 'Windows RT血泪史在开始之前，我们先来说说 Windows RT 是个什么鬼。早在 2012 年 10 月份，微软正式发布了旗下新一代操作系统 Windows 8，声称专门针对触摸屏进行优化，可以在平板电脑等多款配备触摸屏的设备上使用，试图通过一个系统占领所有设备。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=363', '0', '99', '1', '0', 'admin', '1481277524', '1481281287');
