INSERT INTO `v9_python_test` VALUES ('392', '12', '0', 'Xbox部门负责人:微软不会开发自己的VR硬件', '', '', '微软', '微软已经证实他们正在研发的Project Scorpio(天蝎座)主机将支持虚拟现实技术，不过问题是他们如何微软如何来融入VR技术呢?微软会自己推出专门的VR硬件产品么，最近可是有这样的传闻出现。遗憾的是，微软Xbox部门老大证实微软并不会这么做。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=392', '0', '99', '1', '0', 'admin', '1465970060', '1465970813');
