INSERT INTO `v9_python_test` VALUES ('415', '12', '0', '微软押宝AI，能否成就微软霸业！', '', '', '微软', '大型科技公司都尝试通过AI产品和服务来竞争。除了Facebook和Google，IBM、亚马逊和苹果都认为他们对深度的学习的掌握程度就是他们手中的筹码。 以Windows操作系统称霸的微软，有能力成为AI浪潮中的巨头吗？这是很多人想知道答案的问题。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=415', '0', '99', '1', '0', 'admin', '1498703751', '1498703807');
