INSERT INTO `v9_python_test` VALUES ('421', '12', '0', '微软表示“不同意”苹果被评上最环保的科技公司！', '', '', '微软', '苹果被环保组织绿色和平（Greenpeace）连续三年评为最环保的科技公司。苹果获得了“A”评级，苹果清洁能源指数为83%。脸书（facebook）和谷歌也同样获得 “A”评级，清洁能源指数为67%和56%。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=421', '0', '99', '1', '0', 'admin', '1484057988', '1484057993');
INSERT INTO `v9_python_test` VALUES ('422', '12', '0', '微软公布第2批HoloLens发售国家', '', '', '微软', '日前，微软宣布旗下HoloLens增强现实头戴设备HoloLens将登陆更多国家市场，包括澳大利亚、爱尔兰、法国、德国、新西兰和英国。此前，HoloLens 虽早已上市发售，但是最初只针对美国和加拿大市场销售。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=422', '0', '99', '1', '0', 'admin', '1480907760', '1480908013');
