INSERT INTO `v9_python_test` VALUES ('425', '12', '0', '微软公布Win10“三最” 企业版测试用户超96%', '', '', '微软', '天极网笔记本频道随着7月29日Win7/8.1用户免费升级Win10策略结束和8月2日Win10一周年更新正式版发布日期的临近，Win10系统份额正在不断上升，同时Win10安装量已超过3.5亿。现在微软有宣布，已有超过96%的企业用户正在测试Win10。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=425', '0', '99', '1', '0', 'admin', '1468484700', '1468486105');
