INSERT INTO `v9_python_test` VALUES ('434', '12', '0', '2018届校招这些专业可能比较难招人', '', '', '2018校招', '重要事情说五遍校招提前了且看事实2017年5月中上旬，梧桐果新发布2018届校园招聘简章206个，是去年同期的2.26倍，新增2018届招聘职位1564个，是去年的2.57倍，越来越多的企业提前开始布局校园招聘。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=434', '0', '99', '1', '0', 'admin', '1495499520', '1495499652');
