INSERT INTO `v9_python_test` VALUES ('437', '12', '0', '2018五大银行秋季校园招聘重要考点汇总', '', '', '2018校招', '2018银行秋季校园招聘已经开始，招行、交行、兴业、宁波银行等几家银行都已经发校招公告啦！如果你还觉得秋招还很遥远，那些比你还努力的同学都已经网申报名了。下面我们用总结2017年国有五大银行秋季招聘招考信息情况，大家可以根据这些信息，提前做好备考，争取早日考入银行！', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=437', '0', '99', '1', '0', 'admin', '1503474576', '1503474578');
