INSERT INTO `v9_python_test` VALUES ('482', '12', '0', '2018银行校园招聘：分数不够颜值凑', '', '', '2018校招', '相信大家都知道2018年银行校园招聘的报名时间了吧？如果你还不知道，请你多看下往期的文章，因为这个问题，中公金融人（yhzpks）小编真的就差天天强调了。请各位同学一定要记住：2018年银行校园招聘会在今年的9月份开始。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=482', '0', '99', '1', '0', 'admin', '1498013190', '1498013195');
