INSERT INTO `v9_python_test` VALUES ('513', '12', '0', '2018中国移动校招：笔试考试题型、分值解读', '', '', '2018校招', '夏已深则秋不远，马上又要到秋招高峰期了。2018中国移动校园招聘公告将在8、9月份左右发布，今天湖南中公国企（hnyhzpks）的小编为大家整理了2017年中国移动校园招聘考试的考试题型及分数分布，供大家参考。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=513', '0', '99', '1', '0', 'admin', '1496647416', '1496647421');
