INSERT INTO `v9_python_test` VALUES ('518', '12', '0', '百度2018校招全面开启 职场freestyle赋能AI未来', '', '', '2018校招', '继年初人工智能首次写入政府工作报告后，国务院近日印发了我国第一个人工智能(AI)规划《新一代人工智能发展规划》，这标志着人工智能已从一个技术概念上升为一项国家战略。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=518', '0', '99', '1', '0', 'admin', '1501563118', '1501563134');
INSERT INTO `v9_python_test` VALUES ('519', '12', '0', '银行校招的要求和流程，你到底了解多少？', '', '', '2018校招', '最近，马上就迎来暑期，想必很多大三大四党们都去参加了银行暑期实习生招聘的工作。很多小伙伴反映对银行的校园招聘的流程和要求一点也不清楚，那今天小编就和大家唠唠银行校招的流程和要求。帮助各位更好的备战2018秋招。一、银行的招聘形式有哪些？二、银行校园招聘考试的要求1.', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=519', '0', '99', '1', '0', 'admin', '1498620145', '1498620159');
