INSERT INTO `v9_python_test` VALUES ('596', '12', '0', '中国移动校招报考条件，你符合这么要求？', '', '', '2018校招', '2018中国移动校招将在9月份发布招聘公告，那么应聘中国移动，需要什么条件呢，小编就给大家唠唠图片来源网络1.学历要求2018届应届毕业生，全日制本科以上学历2.需要英语四级证书，个别地区有计算机二级证者优先3.', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=596', '0', '99', '1', '0', 'admin', '1503451344', '1503451349');
