INSERT INTO `v9_python_test` VALUES ('625', '12', '0', '2018银行校招：这三所银行钱多事少离家近！', '', '', '2018校招', '2018银行秋招即将拉开帷幕，除了五大行和股份制银行外，三大政策性银行也是热门的报考银行之一，政策性银行是不是真的和传说的那样钱多事少离家近呢？跟着重庆中公金融人一起来了解！三大政策性银行包括：国家开发银行、中国进出口银行、中国农业发展银行三大政策性银行，均直属国务院领导。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=625', '0', '99', '1', '0', 'admin', '1502452737', '1502452746');
