INSERT INTO `v9_python_test` VALUES ('644', '12', '0', '2018银行校招热门职位大揭秘，看你适合什么岗位？', '', '', '2018校招', '2018年银行校园招聘将于9月份陆续开始，这对于所有想要进入银行的应届生们来说，是一场可以决定人生方向的重要考试。在备考期间，考生首先要对银行内部结构有所了解，这样才能根据自身特点和优势，选择一个适合自己的岗位，才能在网申和面试中，一击必中。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=644', '0', '99', '1', '0', 'admin', '1496370956', '1496370963');
INSERT INTO `v9_python_test` VALUES ('645', '12', '0', '2018银行校园招聘，必读的校招流程！', '', '', '2018校招', '2018银行秋招即将开始了，很多伙伴就在想了解秋招的考试流程，今天，小编就和大家详细讲解一下， 银行秋招的考试流程。公告每年的9月、10月各大银行统一发布招聘公告。公告包括：招聘对象、招聘职位、招聘人数、需求专业等内容。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=645', '0', '99', '1', '0', 'admin', '1499136033', '1499136035');
