INSERT INTO `v9_python_test` VALUES ('691', '12', '0', '万字干货｜2017年产品经理校招全程经验总结', '', '', '2018校招', '这份经验总结并不适合手握大把实习经历的大牛们，但除了他们应该还有不少想求职产品经理的同学。大牛的经历只会徒增悲伤，我写的东西或许能够带来一些不一样的感觉。个人相关：哈工大航天硕士，不会技术，没有实习，学校里有参与一些项目，经过一段时间准备后在2017校招中求职产品经理。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=691', '0', '99', '1', '0', 'admin', '1479104450', '1479104454');
