INSERT INTO `v9_python_test` VALUES ('693', '12', '0', '2017年校招全国统一模拟笔试（第一场）编程题集合', '', '', '2018校招', '1 [编程题] 好多鱼！时间限制：1秒空间限制：32768K媛媛有一个鱼缸。鱼缸里面已经有n条鱼，每条鱼的大小为fishSize[i] (1 ≤ i ≤ n,均为正整数)，媛媛现在想把新捕捉的鱼放入鱼缸。鱼缸内存在着大鱼吃小鱼的定律。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=693', '0', '99', '1', '0', 'admin', '1495005559', '1495005562');
