INSERT INTO `v9_python_test` VALUES ('748', '6', '0', '2018最新名企校招集锦丨网易、滴滴等名企校招已抵达', '', '', '2018校招', '你还在等着9月开学才开始网申?你以为大家都是校招愣头青？……错！错！错！人家6月份就已经开始抢人了！有同学已经拿到offer了！提前批、内推没成功的已经在继续网申了！一群老司机已经驶入秋招战场！他喵的都没人通知一下！别急，大佬！当你看到今天的文章的时候还不晚！', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=748', '0', '99', '1', '0', 'admin', '1503381666', '1503381673');
