INSERT INTO `v9_python_test` VALUES ('762', '6', '0', '2018招商银行校招正式启动，你想报吗？', '', '', '2018校招', '前几天，招商银行发了个招聘公告，宣布2018招商银行校园招聘正式启动！2018招商银行校园招聘报考流程：简历投递：8月17日-10月中旬空中宣讲：9月7日校园宣讲：9月全国超过30所重点院校巡回宣讲，现场不只有宣讲哦！', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=762', '0', '99', '1', '0', 'admin', '1503209124', '1503209130');
