INSERT INTO `v9_python_test` VALUES ('782', '6', '0', '2018中国移动校园招聘条件这么高？', '', '', '2018校招', '中国移动想必大家都十分的熟悉，我们也会经常吐槽流量贵呀、话费多呀，如果有个机会让你进中国移动工作，你会去吗？', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=782', '0', '99', '1', '0', 'admin', '1502865434', '1502865443');
