INSERT INTO `v9_python_test` VALUES ('784', '6', '0', '2018银行校园招聘：分数不够颜值凑', '', '', '2018校招', '相信大家都知道2018年银行校园招聘的报名时间了吧？如果你还不知道，请你多看下往期的文章，因为这个问题，中公金融人（yhzpks）小编真的就差天天强调了。请各位同学一定要记住：2018年银行校园招聘会在今年的9月份开始。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=784', '0', '99', '1', '0', 'admin', '1498013190', '1498013195');
INSERT INTO `v9_python_test` VALUES ('785', '6', '0', '2018交通银行总行校园招聘6人公告（IT系统工程师）', '', '', '2018校招', '【银行帮APP】2018交通银行总行数据中心校园招聘公告一出，在此提醒各位考生，2018年交通银行其他分行也将陆续发布！更多银行校园招聘信息等备考问题关注下载银行帮APP！(关注最新校招信息关注银行帮APP)有意报考2018交通银行总行数据中心校园招聘的同学，要开始好好准备喽！', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=785', '0', '99', '1', '0', 'admin', '1502946680', '1502946686');
INSERT INTO `v9_python_test` VALUES ('786', '6', '0', '2018广发银行校园招聘公告，报名条件你都了解吗？', '', '', '2018校招', '资讯、备考、题库、课堂-银行帮APP银行帮APP小编提醒大家：广发银行校园招聘一般在每年的8月份中下旬就发布招聘公告，并开始网上报名，报名时间持续到9月下旬，笔试时间一般在10月下旬。2018广发银行校园招聘公告还暂时未出，小编为此整理了去年的校招公告，可作为参考。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=786', '0', '99', '1', '0', 'admin', '1500862039', '1500862047');
INSERT INTO `v9_python_test` VALUES ('787', '6', '0', '2018四川省秋季校园招聘笔试内容', '', '', '2018校招', '各大银行历年校园招聘笔试的内容和题型大同小异，银行校招的考试内容大体上可分为四大类:行政职业能力测试类（即我们平常说的行测）、英语类、综合知识类、性格测试类。行政职业能力测试类：银行行测考试题型主要以选择题形式出现。主要包括言语理解、数量关系、判断推理、资料分析、常识五大部分。', '0', 'http://127.0.0.1/phpcms/index.php?m=content&c=index&a=show&catid=6&id=787', '0', '99', '1', '0', 'admin', '1502947365', '1502947369');
